// Settings.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Footer from './Footer';
import Header from './Header';

function Settings() {
    // State for sound notifications
    const [soundNotifications, setSoundNotifications] = useState(true);
    // State for selected language
    const [selectedLanguage, setSelectedLanguage] = useState('English');

    // Function to toggle sound notifications
    const toggleSoundNotifications = () => {
        setSoundNotifications(!soundNotifications);
    };

    // Function to handle language selection
    const handleLanguageChange = (language) => {
        setSelectedLanguage(language);
    };

    return (
        <div>
            <Header />
            <div className='mt-5 hai'>
                <div className='container'>
                    <h2>App Settings</h2>
                    {/* General Settings */}
                    <div>
                        <h3>General Settings</h3>
                        <p>App Name: NexusCRM</p>
                        <p>Logo: [Upload Logo]</p>
                        <p>Timezone: [Select Timezone]</p>
                    </div>
                    {/* Email Settings */}
                    <div>
                        <h3>Email Settings</h3>
                        <p>Email Notifications: <Link to="/settings/email-notifications">Manage</Link></p>
                        <p>Email Signature: [Your Email Signature]</p>
                        <p>SMTP Configuration: <Link to="/settings/smtp-configuration">Configure</Link></p>
                    </div>
                    {/* Security Settings */}
                    <div>
                        <h3>Security Settings</h3>
                        <p>Password Policy: <Link to="/settings/password-policy">Configure</Link></p>
                        <p>Two-Factor Authentication: <Link to="/settings/two-factor-auth">Enable/Disable</Link></p>
                        <p>Security Auditing: <Link to="/settings/security-auditing">Configure</Link></p>
                    </div>
                    {/* Notification Settings */}
                    <div>
                        <h3>Notification Settings</h3>
                        <p>Push Notifications: <Link to="/settings/push-notifications">Configure</Link></p>
                        <p>
                            Sound Notifications:
                            <button
                                className={`toggle-button ${soundNotifications ? 'on' : 'off'}`}
                                onClick={toggleSoundNotifications}
                            >
                                {soundNotifications ? 'On' : 'Off'}
                            </button>
                        </p>
                    </div>
                    {/* Appearance Settings */}
                    <div>
                        <h3>Appearance</h3>
                        <p>Theme: [Select Theme]</p>
                        <p>
                            Language:
                            <button
                                className={`language-button ${selectedLanguage === 'English' ? 'selected' : ''}`}
                                onClick={() => handleLanguageChange('English')}
                            >
                                English
                            </button>
                            <button
                                className={`language-button ${selectedLanguage === 'Telugu' ? 'selected' : ''}`}
                                onClick={() => handleLanguageChange('Telugu')}
                            >
                                Telugu
                            </button>
                            <button
                                className={`language-button ${selectedLanguage === 'Spanish' ? 'selected' : ''}`}
                                onClick={() => handleLanguageChange('Spanish')}
                            >
                                Spanish
                            </button>
                            <button
                                className={`language-button ${selectedLanguage === 'Hindi' ? 'selected' : ''}`}
                                onClick={() => handleLanguageChange('Hindi')}
                            >
                                Hindi
                            </button>
                            <button
                                className={`language-button ${selectedLanguage === 'Korean' ? 'selected' : ''}`}
                                onClick={() => handleLanguageChange('Korean')}
                            >
                                Korean
                            </button>
                            <button
                                className={`language-button ${selectedLanguage === 'Arabic' ? 'selected' : ''}`}
                                onClick={() => handleLanguageChange('Spanish')}
                            >
                                Spanish
                            </button>
                            {/* Add more languages as needed */}
                        </p>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default Settings;
