// Settings.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Select, Switch } from 'antd';
import Footer from './Footer';
import Header from './Header';
import './Settings.css'; // Import your CSS file

const { Option } = Select;

function Settings() {
    const [selectedLanguage, setSelectedLanguage] = useState('English');
    const [selectedTheme, setSelectedTheme] = useState('default');
    const [soundNotifications, setSoundNotifications] = useState(true);

    const handleLanguageChange = (language) => {
        setSelectedLanguage(language);
    };

    const handleThemeChange = (theme) => {
        setSelectedTheme(theme);
    };

    const handleSoundNotificationsChange = (checked) => {
        setSoundNotifications(checked);
    };

    return (
        <div className={`app-container ${selectedTheme}`} style={{ direction: selectedLanguage === 'Arabic' ? 'rtl' : 'ltr' }}>
            <Header />
            <div className='mt-5 hai'>
                <div className='container'>
                    <h2>App Settings</h2>
                    {/* General Settings */}
                    <div>
                        <h3>General Settings</h3>
                        <p>App Name: NexusCRM</p>
                        <p>Logo: [Upload Logo]</p>
                        <p>Timezone: [Select Timezone]</p>
                    </div>
                    {/* Email Settings */}
                    <div>
                        <h3>Email Settings</h3>
                        <p>Email Notifications: <Link to="/settings/email-notifications">Manage</Link></p>
                        <p>Email Signature: [Your Email Signature]</p>
                        <p>SMTP Configuration: <Link to="/settings/smtp-configuration">Configure</Link></p>
                    </div>
                    {/* Security Settings */}
                    <div>
                        <h3>Security Settings</h3>
                        <p>Password Policy: <Link to="/settings/password-policy">Configure</Link></p>
                        <p>Two-Factor Authentication: <Link to="/settings/two-factor-auth">Enable/Disable</Link></p>
                        <p>Security Auditing: <Link to="/settings/security-auditing">Configure</Link></p>
                    </div>
                    {/* Notification Settings */}
                    <div>
                        <h3>Notification Settings</h3>
                        <p>Push Notifications: <Link to="/settings/push-notifications">Configure</Link></p>
                        <div className="dropdown-container">
                            <p className="dropdown-label">Sound Notifications:</p>
                            <Switch
                                checkedChildren="On"
                                unCheckedChildren="Off"
                                defaultChecked={soundNotifications}
                                onChange={handleSoundNotificationsChange}
                            />
                        </div>
                    </div>
                    {/* Appearance Settings */}
                    <div>
                        <h3>Appearance</h3>
                        <div className="dropdown-container">
                            <p className="dropdown-label">Theme:</p>
                            <Select value={selectedTheme} style={{ width: 120 }} onChange={handleThemeChange}>
                                <Option value="default">Default</Option>
                                <Option value="dark">Dark</Option>
                                <Option value="light">Light</Option>
                            </Select>
                        </div>
                        <div className="dropdown-container">
                            <p className="dropdown-label">Language:</p>
                            <Select value={selectedLanguage} style={{ width: 120 }} onChange={handleLanguageChange}>
                                <Option value="English">English</Option>
                                <Option value="Telugu">Telugu</Option>
                                <Option value="hindi">Arabic</Option>
                                <Option value="korean">Arabic</Option>
                                <Option value="Arabic">Arabic</Option>
                            </Select>
                        </div>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default Settings;
