import React from 'react';
import Header from './Header';
import '../resources/Aboutus.css';

const AboutUs = () => {
  return (
    <div>
        <Header />
      <div className="aboutus">
        <div className="content">
          <p>
            Welcome to CRM SQUARE, where innovation meets customer-centric solutions! We are a passionate team dedicated to revolutionizing the way businesses manage their customer relationships.
          </p>
          <p>
            At CRM SQUARE, we believe in the power of seamless customer relationship management. Our mission is to empower businesses by providing a comprehensive CRM solution that streamlines interactions, boosts efficiency, and elevates overall performance.
          </p>
          <p>
          What sets us apart is our commitment to understanding the unique needs of each business. We tailor our solutions to ensure they align perfectly with your goals, whether you are a startup, a small business, or an enterprise.
          </p>
          <p>
            Join us on this journey of growth and success. Let CRM SQUARE be your partner in building lasting customer relationships and driving your business forward.
          </p>
        </div>
      </div>
    </div>
  );
};

export default AboutUs;
